  @media (max-width: 800px) {
    justify-content: space-between;
  }
  `;

  const CollapsableMenu = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  overflow: hidden;

  transition: transform .2s ease, opacity .4s ease;
  @media (max-width: 800px) {
    padding: 1.8rem;
    //  display: ${({ isCollapsed }) => (isCollapsed ? 'none' : 'flex')}; 
    flex-direction: column;
    position: absolute;
    left:10%;
    transform-origin: top;
    transform: ${({ isCollapsed }) => isCollapsed && 'translateX(-100%)'};
    opacity: ${({ isCollapsed }) => isCollapsed && '0'};
    background:#000;
    min-height: 1200px;
    z-index: 9;
    left: 0;
    top: 3.9rem;
  }
  `;

  const MenuList = styled.ul`
  padding: 0;
  margin-left:2rem;
  margin-top:0.5rem;

  @media (max-width: 800px) {
    display: flex;
    flex-direction: column;
    text-align: center;
  }
  `;

  const MenuItem = styled(Link)`
  margin-left: 3rem;
  margin-right:0.9rem;
  color: #fff;
  opacity: 0.6;
  text-decoration:none;
  font-size:1.2rem;
  font-weight: bold;
  margin-top:20px;
  line-height:1rem;


  :active,
  :focus,
  :hover {
    opacity: 1;
  }


  @media screen and (max-width : 1600px) {
    font:bold 14px/24px arail;
    padding: 0.1rem 2px;
    margin:0rem 1.9rem 0rem 1.8rem;

  }

  @media screen and (max-width: 1400px) {
    padding 1rem 2px;
    font-size:0.9rem 2px;
    margin-left:10px;
  }

  @media screen and (max-width: 890px) {
    padding 1.4rem 2px;
    font-size:1.4rem;
    margin-left:70px;
  }




`;


const MenuItem2 = styled(Link)`
  margin-left: 3.9rem;

  color: #fff;
  opacity: 0.6;
  text-decoration:none;

  :active,
  :focus,
  :hover {
    opacity: 1;
  }

  display:none;


  @media screen and (max-width: 820px) {
    padding 1.9rem 2px;
    font-size:2rem;
    margin-left:30px;
    display:block;
  }

`;

const BrandLogo = styled(Link)`
  color: #ffd500;
  font-weight: 600;
`;

const StyledMenuBtn = styled.button`
  background: none;

  background-image: ${NetflixIcon};
  width: 4.8rem;
  height: 4.8rem;
  border: none;
  filter: invert(100%);
  display: none;
  @media (max-width: 800px) {
    display: inline-block;
  }
`;

function Navigation (props) {


    const [isCollapsed, setCollapse] = useState('true');
    const [loading, setLoading] = useState(0);
    const [scrollNav, setScrollNav] = useState(false);

    const changeNav = () => {
        if (window.scrollY > 80) {
          setScrollNav(true);
        } else {
          setScrollNav(false);
        }
      };

  useEffect(() => {
    window.addEventListener('scroll', changeNav);
     setLoading(1);

     setTimeout(setLoading(0), 0);

  },[]);

  const handleCollapse = () => {
    setCollapse(!isCollapsed);
  };


    return (
    <StyledNav scrollNav={scrollNav}>

      </StyledNav>
    );
}

export default Navigation
