import React from "react";
import { Link } from "react-router-dom";
import { LeftWrapper, Logo } from "./Main.styled";
import movie from "../../assets/Netflix.png"
import styled from "styled-components";

const MenuList = styled.ul`
   padding: 0;
   margin-left: 2rem;
   margin-top:0.5rem;

   @media (max-width: 800px) {
       display: flex;
       flex-direction: column;
       text-align:center;
   }

`;

const MenuItem = styled.div`
margin-left: 3rem;
margin-right:0.9rem;
color: #fff;
opacity: 0.6;
text-decoration:none;
font-size:1.2rem;
font-weight: bold;
margin-top:20px;
line-height:1rem;

`;

function Left() {
    return (
        <LeftWrapper>
            <Logo>
                <Link to="/react-netflix-framework">
                    <img src={movie} alt="movie"/>
                </Link>
                   <MenuItem>
                    <Link to="/Movie">
                    영화
                    </Link>
                </MenuItem>
                <MenuList>
                </MenuList>
            </Logo>
        </LeftWrapper>
    )
}

export default Left;
