export const primaryColor = '#FF0000';
export const secondaryColr = '#D9D9D9';

const size = {
  mobile: '480px',
  tablet: '768px',
};

export const device = {
  mobile: `(min-width: ${size.mobile})`,
  tablet: `(min-width: ${size.tablet})`,
};

export const light = {
  color: 'black',
  backgroundColor: 'white'
};

export const dark = {
  color: 'white',
  backgroundColor: 'black'
};
