import  Backdrop  from '@material-ui/core/Backdrop';
import  Fade from "@material-ui/core/Fade"
import axios from 'axios';
import { img_original, unvailable } from '../../axios/config/config';
import  Button  from '@material-ui/core';
import YouTubeIcon from "@material-ui/icons/YouTube";
import { GlobalContext } from '../../context/GlobalState'; 
import { api_key } from '../../axios/urls';

const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        width: '90%',
        maxWidth: '780px',
        height: '95%',
        border: '1px solid #282c34',
        borderRadius: 10,
        backgroundColor: "#39445a",
        color: "white",
        boxShadow: theme.shadows[5],
        padding: theme.spacing(1, 1, 3),
    },
    button: {
        // display: 'block',
        // display: 'flex',
        margin: "0px auto",
        justifySelf: 'center',
        // width: '30em',
        maxWidth: 'calc(40em - 20px)',
        // justifyContent: 'center',
        borderRadius: '10px',
    },
    mySvgStyle: {
        fill: 'red',
        // display: 'inline-flex',
        verticalAlign: 'middle',
        // whiteSpace: 'pre',
        // margin: 'auto 0px',
        // alignItems: 'center',
        // justifySelf: 'center',
    },
}));

export default function ContentFocus({children, id, media_type}) {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [movie,setMovie] = useState();
    const [video, setVideo] = useState();

    function getMovie() {
        axios({
            method: "GET",
            url: `https://api.themoviedb.org/3/${media_type}/${id}?api_key=${api_key}&language=ko`
        }).then(response => {
            setMovie(response.data ??[])
        })
    }

