import React, {useState, createContext, useContext} from "react";
import {Container, Inner} from './styles/accordion'

export default function Accordion ({ children, ...restProps }) {
    return (
        <Container {...restProps}>
            <Inner>
                {children}
            </Inner>
        </Container>
    )
}
