    //   return 상품.id == id;
    // });

    return (
        <>
          <Header bg={false}>
              <Header.Frame>
                  <Header.Logo to={ROUTES.HOME} src={logo} alt="넷플릭스" />
              </Header.Frame>
            </Header>


              <Profiles>
                  <Profiles.Title>시청하실 프로필을 선택해주세요</Profiles.Title>
                  <Profiles.List>
                      <Profiles.User
                        // onClick={() => setProfile({ displayName: user.displayName, photoURL: user.photoURL})}
                        // data-testid="user-profile"
                    >
                           <Profiles.Picture src={user.photoURL} />
                          <Profiles.Name>{user.displayName}</Profiles.Name>
                          <div className="user_img">
                              <img 
                                //  src={"/img/profile" + (userdata.id + 1) + ".png"}
                                //  width="100px"
                            ></img>
                        </div>

                        <div className="metadata">
                            <input id="user_name" text="text" value={userdata}>

                            </input>
                        </div>
                    </Profiles.User>
                  </Profiles.List>
              </Profiles>
        </>
    )
}
